        checkElement(map, "0310", cat, asList("01", "02", "03"), null);
        checkElement(map, "0320", cat, asList("01", "02", "03"), null);
        checkElement(map, "0330", cat, asList("01", "02", "03"), null);
        checkElement(map, "0410", cat, asList("01", "02", "03"), null);
